import { useState } from 'react';
import './App.css';
import Name from './Name';

function App() {

  const [name, setName] = useState({
    name:undefined,
    last_name:undefined
  });

  function fetchNameHandler() {
    fetch('http://127.0.0.1:5000/simulator')
    .then((response) => {
      return response.json();
    })
    .then((data) => {
      const obtainedName = data.results[0];
      return {
        name:obtainedName.name,
        last_name:obtainedName.last_name
      };
    });
    setName(obtainedName);
  }

  return (
    <div className="App">
      <header className="App-header">
        <Name name={name} />
        <button onClick={fetchNameHandler}>New Name</button>
      </header>
    </div>
  );
}

export default App;
