import { useState } from 'react';
import './App.css';
import Name from './Name';

function App() {

  const [name, setName] = useState({
    name:undefined,
    last_name:undefined
  });

  function fetchNameHandler() {
    let resp = fetch('http://127.0.0.1:5000/simulator')
    .then((response) => {
      return response.json(); //Si aquí hay un error, quiere decir que el back-end está mal.
    })
    .then((data) => { //Lambda en JS
      setName(data);
    })
  }

  async function addName (name) {
    const response = await fetch('http://127.0.0.1:5000/add_name', {
      method:'POST',
      body: JSON.stringify(name),
      headers: {
        'Content-Type':'application/json'
      }
    });
    const data = await response.json();
    console.log(data);
    alert('Nombre agregado!');
  }

  return (
    <div className="App">
      <header className="App-header">
        <Name name={name} />
        <button onClick={fetchNameHandler}>Nombre Nuevo</button>
        <NewNameForm onSaveName={addName}/>
      </header>
    </div>
  );
}

export default App;
