import React, { useState } from "react";
import axios from "axios";

export function Formulario({ setUser }) {
  const [nombre, setNombre] = useState("");
  const [contraseña, setContraseña] = useState("");
  const [error, setError] = useState(false);

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (nombre === "" || contraseña === "") {
      setError(true);
      return;
    }
    setError(false);

    try {
      const response = await axios.post("http://localhost:5000/login", {
        email: nombre, // Envía el nombre como el email
        password: contraseña,
      });

      if (response.status === 200) {

        // Manejar la respuesta exitosa, por ejemplo, establecer el usuario en el estado.
        setUser(nombre);
      }
    } catch (error) {
      // Manejar errores, por ejemplo, mostrar un mensaje de error al usuario.
      if (error.response.status === 401) {
        console.log("Usuario o contraseña incorrectos");
        setError(true);
        // Puedes mostrar un mensaje de error en tu formulario.
      }
    }
  }

  return (
    <section>
      <h1>Login</h1>

      <form className="formulario" onSubmit={handleSubmit}>
        <label htmlFor="usuario">Usuario:</label>
        <input
          type="text"
          value={nombre}
          onChange={(event) => setNombre(event.target.value)}
        />
        <label htmlFor="contrasena">Contraseña:</label>
        <input
          type="password"
          value={contraseña}
          onChange={(event) => setContraseña(event.target.value)}
        />
        <button>Iniciar sesion</button>
      </form>
      {error && <p>Usuario o contraseña incorrectos</p>}
    </section>
  );
}
