import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export const VerAdmins = () => {
    const [admins, setAdmins] = useState([]);
    const navigate = useNavigate();

    // Fetch all admins from the database
    const getAdmins = async () => {
        const response = await axios.get('http://localhost:5000/admins');
        setAdmins(response.data);
    };

    // Delete an admin from the database
    const deleteAdmin = async (emaile) => {
        console.log(emaile);
        await axios.post('http://localhost:5000/admins', { 
            email: emaile
        });
        getAdmins();
    };

    // Navigate to a page to edit an admin
    const editAdmin = (idUsuario) => {
        navigate(`/editarAdmin/${idUsuario}`);
    };

    // Fetch the admins when the component mounts
    useEffect(() => {
        getAdmins();
    }, []);

    return (
        <div>
            <h1>Lista de admins</h1>
            <table>
                <thead>
                    <tr>
                        <th>Nombre</th>
                        <th>Correo</th>
                        <th>Contrase√±a</th>
                        <th>Editar</th>
                        <th>Eliminar</th>
                    </tr>
                </thead>
                <tbody>
                    {admins.map((admin) => {
                        return (
                            <tr key={admin.idUsuario}>
                                <td>{admin.nombre}</td>
                                <td>{admin.email}</td>
                                <td>{admin.password}</td>
                                <td>
                                    <button onClick={() => editAdmin(admin.idUsuario)}>Editar</button>
                                </td>
                                <td>
                                    <button onClick={() => deleteAdmin(admin.email)}>Eliminar</button>
                                </td>
                            </tr>
                        );
                    })}
                </tbody>
            </table>
        </div>
    );
};
